INSERT INTO `goods` VALUES (47, 'Grokking Algorithms: An Illustrated Guide for Programmers', 'picture/3-3.jpg', 'picture/3-3.jpg', 'picture/3-3.jpg', '33.39', 'Grokking Algorithms is a fully illustrated, friendly guide that teaches you how to apply common algorithms to the practical problems you face every day as a programmer.\r\nYou\'ll start with sorting and searching and, as you build up your skills in thinking algorithmically, \r\nyou\'ll tackle more complex concerns such as data compression and artificial intelligence. \r\nEach carefully presented example includes helpful diagrams and fully annotated code samples in Python.', 10, 3, NULL, NULL, NULL, NULL);
