INSERT INTO `goods` VALUES (51, 'Building Microservices: Designing Fine-Grained Systems 2nd Edition ', 'picture/3-7.jpg', 'picture/3-7.jpg', 'picture/3-7.jpg', '40.99', 'As organizations shift from monolithic applications to smaller, self-contained microservices, distributed systems have become more fine-grained.But developing these new systems brings its own host of problems. This expanded second edition takes a holistic view of topics that you need to consider when building, managing, and scaling microservices architectures.\r\n', 10, 3, NULL, NULL, NULL, NULL);
